<?php// Exit if accessed directlyif (!defined('ABSPATH')) exit;/** * Comments Template * * * @file           comments.php * @package        Responsive * @author         Emil Uzelac * @copyright      2010 - 2012 ThemeID * @license        license.txt * @version        Release: 1.0 * @filesource     wp-content/themes/responsive/comments.php * @link           http://codex.wordpress.org/Theme_Development#Comments_.28comments.php.29 * @since          available since Release 1.0 */// Do not delete these linesif (!empty($_SERVER['SCRIPT_FILENAME']) && 'comments.php' == basename($_SERVER['SCRIPT_FILENAME']))    die ('Пожалуйста, не загружайте эту страницы напрямую. Спасибо!');if (post_password_required()) {    ?>    <p class="nocomments">Введите пароль для просмотра комментариев.</p>    <?php    return;}?><!-- You can start editing here. --><div id="comments">    <h5>        <?php comments_number('Комментариев нет', '1 Комментарии', '% Комментариев'); ?>    </h5></div><?php if (have_comments()) : ?>    <div class="clear"></div>    <div class="commentlist">        <?php wp_list_comments('type=comment&callback=gg_comment'); ?>    </div>    <div class="navigation">        <div class="alignleft"><?php previous_comments_link() ?></div>        <div class="alignright"><?php next_comments_link() ?></div>    </div><?php else : // this is displayed if there are no comments so far ?>    <?php if (comments_open()) : ?>        <!-- If comments are open, but there are no comments. -->    <?php else : // comments are closed ?>        <!-- If comments are closed. -->        <p class="nocomments">Комментарии закрыты.</p>    <?php endif; ?><?php endif; ?><?php if (comments_open()) : ?>    <?php comment_form(); ?><?php endif; // if you delete this the sky will fall on your head ?>